mapper.sql:
where 1=1 

 and mba.id = #{record.id,jdbcType=INTEGER}


 and mba.bulletin_id = #{record.bulletinId,jdbcType=INTEGER}


 and mba.auditor = #{record.auditor,jdbcType=VARCHAR}


 and mba.status = #{record.status,jdbcType=INTEGER}


 and mba.remark = #{record.remark,jdbcType=VARCHAR}


 and mba.create_time = #{record.createTime,jdbcType=TIMESTAMP}


 and mba.update_time = #{record.updateTime,jdbcType=TIMESTAMP}


 and mba.deleted = #{record.deleted,jdbcType=BIT}

mapper.select:
select 

 from mt_bulletin_audit mba


  order by ${order.column}

    desc



  limit #{offset}, #{count}
